let unlock = true;

// Menu
let iconMenus = document.querySelectorAll(".icon-menu");
let menuBodies = document.querySelectorAll(".header__menu");
let menuLinks = document.querySelectorAll(".header__menu-link");

if (iconMenus.length > 0) {
  let delay = 500;
  iconMenus.forEach((iconMenu, index) => {
    iconMenu.addEventListener("click", function (e) {
      if (unlock) {
        body_lock(delay);
        iconMenu.classList.toggle("active");
        menuBodies[index].classList.toggle("active");
      }
    });
  });
}

if (menuLinks.length > 0) {
  menuLinks.forEach(menuLink => {
    menuLink.addEventListener("click", function (e) {
      menu_close();
    });
  });
}

function menu_close() {
  let delay = 500;
  let iconMenus = document.querySelectorAll(".icon-menu");
  let menuBodies = document.querySelectorAll(".header__menu");
  iconMenus.forEach((iconMenu, index) => {
    iconMenu.classList.remove("active");
    menuBodies[index].classList.remove("active");
  });
  body_lock_remove(delay);
}

// BodyLock
function body_lock(delay) {
  let body = document.querySelector("body");
  if (body.classList.contains('_lock')) {
    body_lock_remove(delay);
  } else {
    body_lock_add(delay);
  }
}

function body_lock_remove(delay) {
  let body = document.querySelector("body");
  if (unlock) {
    let lock_padding = document.querySelectorAll("._lp");
    setTimeout(() => {
      for (let index = 0; index < lock_padding.length; index++) {
        const el = lock_padding[index];
        el.style.paddingRight = '0px';
      }
      body.style.paddingRight = '0px';
      body.classList.remove("_lock");
    }, delay);

    unlock = false;
    setTimeout(function () {
      unlock = true;
    }, delay);
  }
}

function body_lock_add(delay) {
  let body = document.querySelector("body");
  if (unlock) {
    let lock_padding = document.querySelectorAll("._lp");
    for (let index = 0; index < lock_padding.length; index++) {
      const el = lock_padding[index];
      el.style.paddingRight = window.innerWidth - document.querySelector('.wrapper').offsetWidth + 'px';
    }
    body.style.paddingRight = window.innerWidth - document.querySelector('.wrapper').offsetWidth + 'px';
    body.classList.add("_lock");

    unlock = false;
    setTimeout(function () {
      unlock = true;
    }, delay);
  }
}

let scr_body = document.querySelector('body');

//ScrollOnClick (Simple)
let goto_links = document.querySelectorAll('.anchor');
if (goto_links) {
	for (let index = 0; index < goto_links.length; index++) {
		let goto_link = goto_links[index];
		goto_link.addEventListener('click', function (e) {
			let target_block_class = goto_link.getAttribute('href').replace('#', '');
			let target_block = document.querySelector('.' + target_block_class);
			_goto(target_block, 300);
			e.preventDefault();
			iconMenu.classList.remove("active");
			menuBody.classList.remove("active");
			scr_body.classList.remove("_lock");
		});
	}
}
function _goto(target_block, speed, offset = 0) {
	let header = 'header';
	//OffsetHeader
	//if (window.innerWidth < 992) {
	//	header = 'header';
	//}
	let options = {
		speedAsDuration: true,
		speed: speed,
		header: header,
		offset: offset,
		easing: 'easeOutQuad',
	};
	let scr = new SmoothScroll();
	scr.animateScroll(target_block, '', options);
}

